import React, { useState, useEffect } from 'react';

const Search = () => {
    const [name, setName] = useState(null)
	const [result, setResult] = useState({result: ""})
	const [update, setUpdate] = useState(false)

	// useEffect(() => {
		// fetch('http://localhost:4999', {
		// 	methods:'GET',
		// 	headers: {
		// 		'Access-Control-Allow-Origin': "*",
		// 		'accept': "application/json"
		// 	}
		// }
		//      ).then(response => response.json()
		// 	 ).then(data => setResult({result: data.result})
		// );
	// 	if (update) {
	// 		alert(result.result);
	// 	}
	// 	setUpdate(false);
	// }, [result.result, update])

	async function handleSubmit(event) {
		event.preventDefault();

		await fetch('http://localhost:4999/result', {
			method:"POST",
			cache: "no-cache",
			mode: "no-cors",
			headers:{
				"content_type":"application/json",
				'accept': "application/json",
				'Access-Control-Allow-Origin': "*",
				'content-type': 'application/x-www-form-urlencoded',
                'Access-Control-Allow-Credentials': 'true',
				"Access-Control-Request-Headers": 'Content-Type, Authorization'
			},
			body:JSON.stringify(name)
			}
		).then(response => {
			console.log(response);
		}).catch(err =>{
			console.log(err);
	    })
		fetch('http://localhost:4999', {
			methods:'GET',
			headers: {
				'Access-Control-Allow-Origin': "*",
				'accept': "application/json"
			}
		}
		     ).then(response => response.json()
			 ).then(data => setResult({result: data.result})
		);
		// setUpdate(true);
		// console.log(update);
		// if (update) {
		// 	alert(result.result);
		// }
		// setUpdate(false);
		console.log(result.result);
		alert(result.result);
	}

	function handleChange(e) {
		setName(e.target.value);
	}

	return (
		<div>
            <form onSubmit={handleSubmit} action="http://localhost:4999/result" method="POST">
                <input type="text" placeholder='Type here...' id='name' name='name' onChange={handleChange}/>
                <br></br>
                <br></br>
                {/* <input type="submit" value="Search"/> */}
                <button type="submit">Search</button>
				{/* {result.result && <p>{result.result}</p>} */}
            </form>
        </div>
	);
};

export default Search;
