import React, { useState, useEffect } from 'react';

const Search = () => {
    const [name, setName] = useState({name: ""})
	const [result, setResult] = useState({result: ""})

	// useEffect(() => {
	// 	fetch('http://localhost:4999/result', {
	// 		methods:'GET',
	// 		headers: {
	// 			'Access-Control-Allow-Origin': "*",
	// 			'Content-Type':'application/json'
	// 		}
	// 	}
	// 	     ).then(response => response.json()
	// 		 ).then(data => setResult({result: data.result})
	// 	);
	// }, [])

	console.log(result);

	async function handleSubmit(event) {
		event.preventDefault();
		await fetch('http://localhost:4999/result', {
			method:"POST",
			cache: "no-cache",
			mode: "no-cors",
			headers:{
				"content_type":"application/json",
				'accept': "application/json",
				'Access-Control-Allow-Origin': "*",
				'content-type': 'application/x-www-form-urlencoded',
                'Access-Control-Allow-Credentials': 'true',
				"Access-Control-Request-Headers": 'Content-Type, Authorization'
			},
			body:JSON.stringify(name)
			}
		).then(response => {
			console.log(response);
		}).catch(err =>{
			console.log(err);
	    })
	}

	function handleChange(e) {
		setName({name: e.target.value});
	}

	return (
		<div>
            <form onSubmit={handleSubmit} action="http://localhost:4999/result" method="POST">
                <input type="text" placeholder='Type here...' id='name' name='name' onChange={handleChange}/>
                <br></br>
                <br></br>
                {/* <input type="submit" value="Search"/> */}
                <button type="submit">Search</button>
				<p>{result.result}</p>
            </form>
        </div>
	);
};

export default Search;
