import React, { useState } from 'react';
import Modal from "react-modal";
import FileSaver from 'file-saver';
import file from '..../public/resources';

const Resource = () => {
    const [isOpen, setIsOpen] = useState(false);

	function toggleModal() {
		setIsOpen(!isOpen);
	}

	const download = () => {
        FileSaver.saveAs(
            process.env.PUBLIC_URL + '/resource/resources.pdf',
            "Resources.pdf"
        );
    }

	return (
		<div>
            <h5>Can't find what you want? You can download more resources <button onClick={toggleModal}>here</button>.
                <Modal
                    isOpen={isOpen}
                    onRequestClose={toggleModal}
                    // contentLabel="My dialog"
                    className="secondmodal"
                    overlayClassName="secondoverlay"
                    closeTimeoutMS={500}
                >
                    <div>Are you sure you want to download a list of resources?</div>
                    <br></br>
                    <button onClick={download}>Yes</button>
                    <a href={file} download="resources.pdf">Download</a>
                </Modal>
            </h5>
        </div>
	);
};

export default Resource;
