import React, { useState, useEffect } from 'react';

const Search = () => {
    const [name, setName] = useState(null)
	const [nutrient, setNutrient] = useState({nutrient: ""})

	async function handleSubmit(event) {
		event.preventDefault();
		await fetch('http://localhost:4999/result', {
			method:"POST",
			cache: "no-cache",
			mode: "no-cors",
			headers:{
				"content_type":"application/json",
				'accept': "application/json",
				'Access-Control-Allow-Origin': "*",
				'content-type': 'application/x-www-form-urlencoded',
                'Access-Control-Allow-Credentials': 'true',
				"Access-Control-Request-Headers": 'Content-Type, Authorization'
			},
			body:JSON.stringify(name)
			}
		).then(response => {
			console.log(response);
		}).catch(err =>{
			console.log(err);
	    })
	}

	function handleChange(e) {
		setName(e.target.value);
	}

	// console.log(nutrient);

	// useEffect(() => {
	// 	async function fetchData() {
	// 		await fetch('http://localhost:4999/result', {
	// 			method:"GET",
	// 			cache: "no-cache",
	// 			mode: "no-cors",
	// 			headers:{
	// 				'Access-Control-Allow-Origin': "*"
	// 		    }}
	// 		).then(
	// 			response => response.json()
	// 		).then(data => setNutrient({nutrient: data.nutrient}))	  
	// 	}
	// 	fetchData();
	//   }, [])

	useEffect(() => {
		fetch('http://localhost:4999/result'
		     ).then(response => response.json()
			 ).then(data => setNutrient({nutrient: data.nutrient})
		);
	  }, [])

	return (
		<div>
            <form onSubmit={handleSubmit} action="http://localhost:4999/result" method="POST">
                <input type="text" placeholder='Type here...' id="name" onChange={handleChange}/>
                <br></br>
                <br></br>
                {/* <input type="submit" value="Search"/> */}
                <button type="submit">Search</button>
				<p>{nutrient.nutrient}</p>
            </form>
        </div>
	);
};

export default Search;
