import React, { useState } from 'react';
import { useNav } from '../customHooks/useNav';
import './Page.css';
import ReactTooltip from "react-tooltip";
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';
import styled from "styled-components";
import Modal from "react-modal";

Modal.setAppElement("#root");

const DropDownContainer = styled("div")`
  width: 18.5em;
  margin: auto;
  font-size: small;
`;

const Tool = () => {
	// useNav takes in a navLinkId and returns a ref
	// this ref is used to register the navLinkId that's
	// currently in view, and apply activeClass styling
	// to the corresponding nav childElement

	const toolRef = useNav('Tool');
	const options = [
		'(Optional)Select More',
		'Your Protein Info With Easy Meal Prep Ideas',
		'Your Protein Info Compared To Other Proteins'
	];
	const defaultOption = options[0];
	let url="https://docs.google.com/document/d/1SkNB97ANkPkOcQ53IpB98OxLE0t9H9Hu69QxYFsj82c/edit?usp=sharing";

	const [isOpen, setIsOpen] = useState(false);

	function toggleModal() {
	  setIsOpen(!isOpen);
	}
	return (
		<section ref={toolRef} id='toolContainer'>
			<img
				src='https://source.unsplash.com/random/600x600/?food,healthy meals'
				alt='unsplash-img'
			/>
			<div>
				<h3>Use the search tool to help you make smart decisions on selecting food protein.</h3>
				<div>
					<div>
						<h4 data-tip data-for="searchTip">Start Your Search(Hover for a demo):</h4>
						<input type="text" placeholder='Type here...'/>
					</div>
					<DropDownContainer>
						<Dropdown padding='0' options={options} onChange={onchange} value={defaultOption} placeholder="Select an option" />
					</DropDownContainer>
					{/* <br></br> */}
					<button onClick={toggleModal}>Search</button>
					<Modal
						isOpen={isOpen}
						onRequestClose={toggleModal}
						contentLabel="My dialog"
						className="mymodal"
						overlayClassName="myoverlay"
						closeTimeoutMS={500}
					>
						<div class="modal">3 oz. (86 g) boneless, skinless grilled chicken breast has 26g protein.
						</div>
						<br></br>
						<button onClick={toggleModal}>Undo</button>
					</Modal>
					<br></br>
					<a href={url} target="_blank" rel="noreferrer">FAQs</a>
					<br></br>
					<div>
						<h5>Can't find what you want? You can download more resources 
							<a href=" "> here</a>.
							<Modal
								isOpen={isOpen}
								onRequestClose={toggleModal}
								contentLabel="My dialog"
								className="mymodal"
								overlayClassName="myoverlay"
								closeTimeoutMS={500}
							>
							</Modal>
						</h5>
					</div>
					<ReactTooltip id="searchTip" place="right" type="info" effect="solid">
						<img src={require('/Users/home/cs361/smart-proteins/src/assets/demo.gif')} alt="React Logo" />
					</ReactTooltip>
				</div>
			</div>

		</section>
	);
};

export default Tool;
