import React, { useState, useEffect } from 'react';

const Search = () => {
    const [name, setName] = useState(null)
	const [result, setResult] = useState({result: ""})
	// const [update, setUpdate] = useState(false)

	// useEffect(() => {
	// 	async function fetchData() {
	// 		// setUpdate(true);
	// 		fetch('http://localhost:4999', {
	// 			methods:'GET',
	// 			headers: {
	// 				'Access-Control-Allow-Origin': "*",
	// 				'accept': "application/json"
	// 			}
	// 		}
	// 			 ).then(response => response.json()
	// 			 ).then(data => setResult({result: data.result})
	// 			 ).then(() => alert(result.result)
	// 		);


	// 	}

	// 	fetchData();

	// }, [])

	function handleSubmit(event) {
		event.preventDefault();

		fetch('http://localhost:4999/result', {
			method:"POST",
			cache: "no-cache",
			mode: "no-cors",
			headers:{
				"content_type":"application/json",
				'accept': "application/json",
				'Access-Control-Allow-Origin': "*",
				'content-type': 'application/x-www-form-urlencoded',
                'Access-Control-Allow-Credentials': 'true',
				"Access-Control-Request-Headers": 'Content-Type, Authorization'
			},
			body:JSON.stringify(name)
			}
		).then(response => {
			console.log(response);
		}).catch(err =>{
			console.log(err);
	    })
		fetch('http://localhost:4999', {
				methods:'GET',
				headers: {
					'Access-Control-Allow-Origin': "*",
					'accept': "application/json"
				}
			}
				 ).then(response => response.json()
				 ).then(data => setResult({result: data.result})
				//  ).then(() => alert(result.result)
			);
	}

	function handleChange(e) {
		setName(e.target.value);
	}

	return (
		<div>
            <form onSubmit={handleSubmit} action="http://localhost:4999/result" method="POST">
                <input type="text" placeholder='Type here...' id='name' name='name' onChange={handleChange}/>
                <br></br>
                <br></br>
                {/* <input type="submit" value="Search"/> */}
                <button type="submit">Search</button>
				{/* {result.result && <p>{result.result}</p>} */}
            </form>
        </div>
	);
};

export default Search;
